mapper.select:
select distinct assignee from tb_issue
and project_name = #{projectName}
group by assignee asc

select assignee,sum(story_point) as story_point ,DATE_FORMAT(created,'%Y-%m-%d') AS new_created from tb_issue
and assignee in
#{item}
and created between #{RequestVo.startTime} and #{RequestVo.endTime}
group by new_created asc

select assignee,issue_key,all_original_estimate,all_remaining_estimate,project_name from tb_issue
and assignee in
#{item}
and resolution = 0 and created between #{RequestVo.startTime} and #{RequestVo.endTime}
order by issue_key asc

select assignee,sum(story_point) as story_point,sum(all_time_spent) as all_time_spent from tb_issue
and assignee in
#{item}
and resolution = 1 and created between #{RequestVo.startTime} and #{RequestVo.endTime}
group by assignee

select assignee,resolution,project_name,issue_key,all_original_estimate,all_time_spent
from tb_issue

    and assignee in

        #{item}

    and project_name in

          #{item}

    and created between #{RequestVo.startTime} and #{RequestVo.endTime}

select assignee,project_name,resolution,created,sum(story_point) as story_point
from tb_issue

    and project_name in

        #{item}

    and resolution = #{resolution}
    and created between #{RequestVo.startTime} and #{RequestVo.endTime}

group by assignee,project_name,resolution,created asc

select project_name , sum(story_point) as story_point ,sum(all_time_spent) as all_time_spent from tb_issue
and project_name in
#{item}
and resolution = 1 and created between #{RequestVo.startTime} and #{RequestVo.endTime}
group by project_name

select  project_name ,assignee,sum(story_point) as story_point,sum(all_time_spent) as all_time_spent
from  tb_issue

    and project_name in

        #{item}

    and assignee in

        #{item}

    and resolution = 1
    and created between #{RequestVo.startTime} and #{RequestVo.endTime}

group by assignee
